try {
  const inputData = $input.first().json;
  const query = inputData.query || {};
  const body = inputData.body || {};
  const headers = inputData.headers || {};
  const method = headers['x-n8n-request-method'] || 'GET';

  console.log('=== REQUEST RECEIVED ===');
  console.log('Method:', method);
  console.log('Query params:', JSON.stringify(query, null, 2));
  console.log('Body:', JSON.stringify(body, null, 2));

  // Handle GET requests (WhatsApp verification)
  if (method === 'GET' && query['hub.mode'] && query['hub.challenge'] && query['hub.verify_token']) {
    console.log('âœ… WHATSAPP VERIFICATION REQUEST');
    const mode = query['hub.mode'];
    const challenge = query['hub.challenge'];
    const verifyToken = query['hub.verify_token'];

    if (mode === 'subscribe' && verifyToken === 'eventAI') {
      return {
        action: 'verify',
        challenge: challenge,
        status: 'verified'
      };
    } else {
      return {
        action: 'verify_failed',
        error: 'Invalid token: ' + verifyToken
      };
    }
  }

  // Handle POST requests (WhatsApp messages)
  if (method === 'POST' && body.entry && Array.isArray(body.entry)) {
    console.log('âœ… WHATSAPP MESSAGE REQUEST');

    for (const entry of body.entry) {
      if (entry.changes && Array.isArray(entry.changes)) {
        for (const change of entry.changes) {
          if (change.value && change.value.messages && Array.isArray(change.value.messages)) {
            const message = change.value.messages[0]; // Get first message
            if (message && message.text && message.text.body) {
              console.log('ðŸ“± Message found:', message.text.body);
              console.log('ðŸ“± From:', message.from);

              return {
                action: 'process',
                userMessage: message.text.body,
                userPhone: message.from,
                messageId: message.id,
                timestamp: new Date().toISOString()
              };
            }
          }
        }
      }
    }

    console.log('âš ï¸ No valid message found in POST body');
    return {
      action: 'skip',
      reason: 'no_valid_message',
      body: body
    };
  }

  // Handle test message via GET parameters (for testing)
  if (method === 'GET' && query.message && query.from) {
    console.log('âœ… TEST MESSAGE VIA GET');
    console.log('Message:', query.message);
    console.log('From:', query.from);

    return {
      action: 'process',
      userMessage: query.message,
      userPhone: query.from,
      timestamp: new Date().toISOString()
    };
  }

  // Default response for any other request
  console.log('â„¹ï¸ GENERAL REQUEST');
  return {
    action: 'info',
    message: 'WhatsApp webhook is working',
    method: method,
    availableEndpoints: {
      verification: 'GET with hub.mode=subscribe&hub.challenge=TEST&hub.verify_token=eventAI',
      whatsappMessage: 'POST with WhatsApp message format',
      testMessage: 'GET with message=Hello&from=60189154390'
    },
    timestamp: new Date().toISOString()
  };

} catch (error) {
  console.log('âŒ ERROR:', error.message);
  console.log('âŒ Stack:', error.stack);
  return {
    action: 'error',
    error: error.message,
    timestamp: new Date().toISOString()
  };
}

// Prepare context for OpenAI from events data
const userInput = $input.first().json;
const eventsData = $input.all().slice(1); // Skip the first item (user input)

console.log('=== PREPARING RAG CONTEXT ===');
console.log('User message:', userInput.userMessage);
console.log('Found events:', eventsData.length);

// Format events data for context
let eventsContext = 'AVAILABLE EVENTS DATABASE:\n\n';

if (eventsData.length === 0) {
  eventsContext += 'No upcoming events found in the database.\n';
} else {
  eventsData.forEach((event, index) => {
    const eventData = event.json;
    eventsContext += `Event ${index + 1}:\n`;
    eventsContext += `- ID: ${eventData.event_id}\n`;
    eventsContext += `- Name: ${eventData.name}\n`;
    eventsContext += `- Description: ${eventData.description || 'No description'}\n`;
    eventsContext += `- Venue: ${eventData.venue || 'Venue TBD'}\n`;
    eventsContext += `- Start: ${new Date(eventData.date_of_event_start).toLocaleString()}\n`;
    eventsContext += `- End: ${new Date(eventData.date_of_event_end).toLocaleString()}\n`;
    eventsContext += `- Status: ${eventData.status}\n`;
    eventsContext += `- Organizer: ${eventData.user_email}\n`;
    eventsContext += `- Has Forecast: ${eventData.has_forecast}\n\n`;
  });
}

// Create system prompt
const systemPrompt = `You are an AI assistant for Event AI, a smart event management platform. You help users find information about events, provide recommendations, and answer questions about our event database.

Your capabilities:
- Answer questions about upcoming events
- Provide event recommendations based on user preferences
- Help with event details like dates, venues, descriptions
- Explain event forecasting and crowd predictions
- General event management advice

Guidelines:
- Be helpful, friendly, and concise
- Use the events database information provided
- If an event has forecast data, mention it as a feature
- For questions outside your knowledge, politely redirect to event-related topics
- Always format responses for WhatsApp (use emojis, short lines)
- Keep responses under 1000 characters for WhatsApp limits

Current Events Database:
${eventsContext}`;

const userQuery = `User Question: ${userInput.userMessage}

Please provide a helpful response based on the available events data. Format your response for WhatsApp messaging.`;

console.log('âœ… Context prepared for OpenAI');
console.log('Events in context:', eventsData.length);

return {
  systemPrompt,
  userQuery,
  userPhone: userInput.userPhone,
  originalMessage: userInput.userMessage,
  eventsCount: eventsData.length,
  timestamp: userInput.timestamp
};

// Process OpenAI response and prepare for WhatsApp
const contextData = $input.first().json;
const openaiResponse = $input.last().json;

console.log('=== PROCESSING AI RESPONSE ===');
console.log('Context data:', JSON.stringify(contextData, null, 2));
console.log('OpenAI Response:', openaiResponse.message?.content || openaiResponse.content || 'No response');

// Extract AI response text
let aiResponse = '';
if (openaiResponse.message && openaiResponse.message.content) {
  aiResponse = openaiResponse.message.content;
} else if (openaiResponse.content) {
  aiResponse = openaiResponse.content;
} else if (typeof openaiResponse === 'string') {
  aiResponse = openaiResponse;
} else {
  aiResponse = 'Sorry, I encountered an issue processing your request. Please try again.';
}

// Clean up response for WhatsApp
aiResponse = aiResponse.trim();

// Ensure response is within WhatsApp limits (approx 1000 chars)
if (aiResponse.length > 1000) {
  aiResponse = aiResponse.substring(0, 950) + '... (truncated)';
}

// Add Event AI signature
aiResponse += '\n\nðŸ¤– *Event AI Assistant*';

console.log('âœ… Response prepared for WhatsApp');
console.log('Final response length:', aiResponse.length);
console.log('User phone:', contextData.userPhone);

// Make sure userPhone is properly included
const userPhone = contextData.userPhone || '60189154390'; // fallback

return {
  userPhone: userPhone,
  responseMessage: aiResponse,
  originalUserMessage: contextData.originalMessage || contextData.userMessage,
  eventsCount: contextData.eventsCount,
  timestamp: new Date().toISOString(),
  processingComplete: true
};
